mapper.select:
select , e.LastName AS EmployeeLastName, e.FirstName AS EmployeeFirstName, c.CompanyName AS CustomerName, s.CompanyName AS ShipViaName from example_orders AS t INNER JOIN example_customers AS c ON t.CustomerID = c.CustomerID INNER JOIN example_employees AS e ON t.EmployeeID = e.EmployeeID INNER JOIN example_shippers AS s ON t.ShipVia = s.ShipperID
AND t.OrderID = #{id}
AND t.OrderDate >= #{beginTime}
AND t.OrderDate < #{endTime}
AND ( t.ShipName LIKE #{keyword} OR t.ShipCity LIKE #{keyword} OR t.ShipRegion LIKE #{keyword} OR t.ShipPostalCode LIKE #{keyword} OR t.ShipCountry LIKE #{keyword} OR e.LastName LIKE #{keyword} OR e.FirstName LIKE #{keyword} OR c.CompanyName LIKE #{keyword} OR s.CompanyName LIKE #{keyword} )
